import React from 'react';
import '../index.css';

const openSide = () => {
    let sidebar = document.getElementById('side--menu').style.width

    for (let i=0; i<sidebar.lenght;i++) {
        if (sidebar === '0') {
            document.getElementById('side--menu').style.width = "70%";
            document.getElementById('header').style.marginLeft = "70%";
            document.getElementById('main').style.marginLeft = "70%";
        } else {
            document.getElementById('side--menu').style.width = '0';
            document.getElementById('header').style.marginLeft = '0';
            document.getElementById('main').style.marginLeft = '0';
        }
    }
};

const Sidebar = () => {
    return (
        <sidebar className='sidebar'>
            <div id='side--menu-button'>
                <button className='side--menu-button' onClick={openSide}>&#9776;</button>
            </div>
            <div id='side--menu'>
                <ul>
                    <li className="side--item"><a href='./about-me.jsx' className="side--item">ABOUT ME</a></li>
                    <li className="side--item"><a href='./astrology.jsx' className="side--item">ASTROLOGY</a></li>
                    <li className="side--item"><a href='./saama.jsx' className="side--item">SAAMA</a></li>
                    <li className="side--item"><a href='./accompaniment.jsx' className="side--item">ACCOMPANIMENT</a></li>
                    <li className="side--item"><a href='./readings.jsx' className="side--item">READINGS</a></li>
                </ul>
            </div>
        </sidebar>
    )
}


export default Sidebar
