import '../index.css';

const Sidebar = () => {
    return (
        <sidebar class='sidebar'>
            <div id='side--menu-button' className='side--show'>
                <button>Menu</button>
            </div>
            <div className='side--hide'>
                <ul>
                    <li className="side--item"><a href='./about-me.jsx'>ABOUT ME</a></li>
                    <li className="side--item"><a href='./astrology.jsx'>ASTROLOGY</a></li>
                    <li className="side--item"><a href='./saama.jsx'>SAAMA</a></li>
                    <li className="side--item"><a href='./accompaniment.jsx'>ACCOMPANIMENT</a></li>
                    <li className="side--item"><a href='./readings.jsx'>READINGS</a></li>
                </ul>
            </div>
        </sidebar>
    )
}

